import { useState } from 'react'

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState({
    good: 0,
    neutral: 0,
    bad: 0,
    total: 0
  })
  const [average, setAverage] = useState(0)

  const handleGood = () => {
    const weight = 1
    const updatedGood = good + 1
    setGood(updatedGood)
    setAll({ ...all, good: all.good + 1, total: all.total + 1 })
    console.log(all.total)
    all.bad === 0 ? setAverage(updatedGood) :
      setAverage(updatedGood + (all.bad * -1) / all.total)
  }

  const handleNeutral = () => {
    const weight = 0
    const updatedNeutral = neutral + 1
    setNeutral(updatedNeutral)
    setAll({ ...all, neutral: all.neutral + 1, total: all.total + 1 })
    setAverage(all.good + (all.bad * -1) / all.total)
  }

  const handleBad = () => {
    const weight = -1
    const updatedBad = bad + 1
    setBad(updatedBad)
    setAll({ ...all, bad: all.bad + 1, total: all.total + 1 })
    setAverage(all.good + (updatedBad * -1) / all.total)
  }



  return (
    <div>
      <h2>give feedback</h2>
      <button onClick={handleGood}>good</button>
      <button onClick={handleNeutral}>neutral</button>
      <button onClick={handleBad}>bad</button>
      <h2>statistics</h2>
      <p>good {good}</p>
      <p>neutral {neutral}</p>
      <p>bad {bad}</p>
      <p>all {all.total}</p>
      <p>average {average}</p>
      <p>positive %</p>
    </div>
  )
}

export default App
