import { useState } from 'react'
import './App.css'
const Button = ({ handleClick, text }) => {
  console.log(handleClick, text)
  return (
    <button onClick={handleClick}>{text}</button>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [average, setAverage] = useState(0)
  const [totalReviews, setTotalReviews] = useState(0)

  const incGood = () => {
    const updatedGood = good + 1
    const updatedReviews = totalReviews + 1
    setGood(updatedGood)
    setTotalReviews(updatedReviews)
    setAverage(updatedGood + bad * -1 / updatedReviews)
  }
  const incNeutral = () => {
    const updatedReviews = totalReviews + 1
    const updatedNeutral = neutral + 1
    setNeutral(updatedNeutral)
    setTotalReviews(updatedReviews)
    setAverage(bad * -1 + good + neutral / updatedReviews)
  }
  const incBad = () => {
    const updatedBad = bad + 1
    const updatedReviews = totalReviews + 1
    setBad(updatedBad)
    setTotalReviews(updatedReviews)
    setAverage(updatedBad * -1 + good / updatedReviews)
  }


  return (
    <div className='container'>
      <h2 id='feedback'>give feedback</h2>
      <div className='buttons_container'>

        <Button handleClick={incGood} text='Good' />
        <Button handleClick={incNeutral} text='Neutral' />
        <Button handleClick={incBad} text='Bad' />
      </div>
      <div className='results_container'>
        <h2>statistics</h2>
        <div className='results'>
          <h3>Good: {good}</h3>
          <h3>Neutral: {neutral}</h3>
          <h3>Bad: {bad}</h3>
        </div>
        Average: {average}
        <br></br>
        Total Reviews: {totalReviews}
      </div>
    </div>
  )
}

export default App
