import axios from 'axios'
import Filter from "./components/Filter";
import { useState, useEffect } from "react";
import countryServices from './services/country'
function App() {
  const [filteredSearch, setFilteredSearch] = useState('')
  const [countries, setCountries] = useState([])

  useEffect(() => {
    countryServices
      .getAll()
      .then(initialCountries => {
        setCountries(initialCountries)
      })
  }, [])

  const handleFilteredSearch = (e) => {
    setFilteredSearch(e.target.value)
    console.log(e.target.value)
    /*   console.log(filteredSearch, handleFilteredSearch) */
    const filteredCountries = countries.filter(country => country.name.common.toLowerCase().includes(filteredSearch.toLowerCase()))
    console.log(filteredCountries)
  }

  return (
    <div className="App">
      <Filter filteredSearch={filteredSearch} handleFilteredSearch={handleFilteredSearch} />
    </div>
  );
}

export default App;
