import axios, { all } from 'axios'
import Filter from "./components/Filter";
import Content from './components/Content';
import { useState, useEffect } from "react";
import countryServices from './services/country'
function App() {
  const [filteredSearch, setFilteredSearch] = useState('')
  const [allCountries, setAllCountries] = useState([])
  const [countries, setCountries] = useState([])

  useEffect(() => {
    countryServices
      .getAll()
      .then(initialCountries => {
        setAllCountries(initialCountries)
      })
  }, [])

  const handleFilteredSearch = (e) => {
    setFilteredSearch(e.target.value)
    //console.log(e.target.value)
    /*   console.log(filteredSearch, handleFilteredSearch) */
    const filteredCountries = allCountries.filter(country => country.name.common.toLowerCase().includes(filteredSearch.toLowerCase()))
    if (e.target.value === '') {
      setCountries([])
    }
    else if (filteredCountries.length > 10) {
      return (
        <p>Too many matches, specify another filter</p>
      )
    }
    else if (filteredCountries.length <= 10) {
      setCountries(filteredCountries.map((country => {
        return (
          <p key={country.name.common}>{country.name.common}</p>
        )
      })))
    }
    //console.log(filteredCountries)
  }

  return (
    <div className="App">
      <Filter
        filteredSearch={filteredSearch}
        handleFilteredSearch={handleFilteredSearch}
      />
      <Content
        allCountries={allCountries}
        countries={countries}
        filteredSearch={filteredSearch}
      />
      {countries}
    </div>
  );
}

export default App;
