import React from 'react'
import '../App.css'

/* const Header = ({ name }) => {
    return (
        <h2>{name}</h2>
    )
} */

const Header = ({ name }) => {
    return <h2>{name}</h2>
}
const Content = ({ parts }) => {
    return (
        <div>
            {parts.map((part) => (
                <Part key={part.id} name={part.name} exercises={part.exercises} />
            ))}
        </div>
    )
}
const Part = ({ name, exercises }) => {
    return (
        <p>{name} {exercises}</p>
    )
}
const Total = ({ parts }) => {
    const total = parts.reduce((sum, part) => {
        return sum + part.exercises
    }, 0)

    return <strong>total of {total} exercises</strong>
}
const Course1 = ({ course }) => {
    return (
        <div>
            <Header name={course.name} />
            <Content parts={course.parts} />
            <Total parts={course.parts} />
        </div>
    )
}


const Course = ({ course }) => {
    //console.log(course)
    //console.log(course.parts.exercises)
    const parts = course.parts.map(x => x.exercises)
    const sum = parts.reduce((sum, part) => {
        return sum + part
    }, 0)
    return (
        <>   <div>
            <Header name={course.name} />
            <Content parts={course.parts} />
            <Total parts={course.parts} />
        </div>
            {/*   <div>
                <h2>{course.name}</h2>

                {course.parts.map((x, i) =>
                    <li className='list-item' key={x.id}>
                        {x.name} {x.exercises}
                    </li>)}

                <p><strong>total of {sum} exercises</strong></p>
            </div> */}
        </>
    )
}

export default Course
