import React from 'react'
import '../App.css'

const Header = ({ name }) => {
    return (
        <h2>{name}</h2>
    )
}

const Course = ({ course }) => {
    console.log(course)
    console.log(course.parts.exercises)
    const parts = course.parts.map(x => x.exercises)
    const sum = parts.reduce((sum, part) => {
        return sum + part
    }, 0)
    return (
        <div>

            <ul>
                {course.parts.map((x, i) =>
                    <li className='list-item' key={x.id}>
                        {x.name} {x.exercises}
                    </li>)}
            </ul>
            <p><strong>total of {sum} exercises</strong></p>
        </div>
    )
}

export default Course
