import { useState } from 'react'

const Filter = ({ filteredSearch, handleFilteredSearch }) => {
  return (
    <p>filter shown with <input value={filteredSearch} onChange={handleFilteredSearch} /></p>
  )
}

const PersonForm = ({ addPerson, newName, handleNameChange, newNumber, handleNumberChange }) => {
  console.log(addPerson, newName, handleNameChange, newNumber, handleNumberChange)
  return (
    <form onSubmit={addPerson}>
      <h2>add a new</h2>
      <div>
        name: <input value={newName} onChange={handleNameChange} />
      </div>
      <div>number: <input value={newNumber} onChange={handleNumberChange} /></div>
      {/* <div>debug: {newName}</div> */}
      <div>
        <button type="submit">add</button>
      </div>
    </form>
  )
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filteredSearch, setFilteredSearch] = useState('')

  const handleFilteredSearch = (e) => {
    //console.log(e.target.value)
    setFilteredSearch(e.target.value)
  }

  const handleNameChange = (e) => {
    setNewName(e.target.value)
    persons.map((person) => {
      if (person.name.toLowerCase() === e.target.value.toLowerCase()) {
        //console.log(person.name, e.target.value)
        /* return */ alert(`${person.name} is already added to phonebook`)
      }
    })
    //console.log(newName)
  }
  const handleNumberChange = (e) => {
    setNewNumber(e.target.value)
    persons.map((person) => {
      if (person.number === e.target.value) {
        //console.log(person.name, e.target.value)
        /* return */ alert(`${person.number} is already added to phonebook`)
      }
    })
    //console.log(newName)
  }
  const addPerson = (e) => {
    e.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber,
      id: newName,
    }
    setPersons(persons.concat(personObject))
    setNewName('')
    setNewNumber('')
  }

  const personsToShow = filteredSearch === ''
    ? persons
    : persons.filter(person => person.name.toLowerCase().includes(filteredSearch.toLowerCase()))

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filteredSearch={filteredSearch} handleFilteredSearch={handleFilteredSearch} />
      <PersonForm
        addPerson={addPerson}
        newName={newName}
        handleNameChange={handleNameChange}
        newNumber={newNumber}
        handleNumberChange={handleNumberChange} />
      <h2>Numbers</h2>
      {personsToShow.map(person =>
        <p key={person.id}>{person.name} {person.number}</p>
      )}
      {/*   {persons.map((person) => <li key={person.id}>{person.name} {person.number}</li>)} */}
      ...
    </div>
  )
}

export default App
