import { useState, useEffect } from 'react'
import axios from 'axios'

import personService from './services/persons'

import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import Persons from './components/Persons'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filteredSearch, setFilteredSearch] = useState('')

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      })
  }, [])

  const handleFilteredSearch = (e) => {
    //console.log(e.target.value)
    setFilteredSearch(e.target.value)
  }

  const handleNameChange = (e) => {
    setNewName(e.target.value)
    persons.map((person) => {
      if (person.name.toLowerCase() === e.target.value.toLowerCase()) {
        //console.log(person.name, e.target.value)
        /* return */ alert(`${person.name} is already added to phonebook`)
      }
    })
    //console.log(newName)
  }
  const handleNumberChange = (e) => {
    setNewNumber(e.target.value)
    persons.map((person) => {
      if (person.number === e.target.value) {
        //console.log(person.name, e.target.value)
        /* return */ alert(`${person.number} is already added to phonebook`)
      }
    })
    //console.log(newName)
  }
  const addPerson = (e) => {
    e.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber,
      id: newName,
    }
    let result
    let alreadyAdded = persons.map((person) => {
      if (person.number === newName) {
        result = window.confirm(`${person.name} is already added to phonebook, replace
       the old number with a new ${newNumber}?`)
      }
    })
    personService
      .create(personObject)
      .then(returnedPerson => {
        setPersons(persons.concat(returnedPerson))
        setNewName('')
        setNewNumber('')
      })
    /*  axios
       .post('http://localhost:3001/persons', personObject)
       .then(response => {
         setPersons(persons.concat(response.data))
         setNewName('')
         setNewNumber('')
       }) */
    /*  request.then(response => response.data)
     setPersons(persons.concat(personObject))
     setNewName('')
     setNewNumber('') */
  }

  const deletePerson = id => {
    let person = persons.find(p => p.id === id)
    person = person.name
    let result = window.confirm(`Delete ${person} ?`)
    if (result === true) {


      console.log(`delete id:${id}`)

      /*  const changedPerson = {...person, } */
      personService
        .remove(id)
        .then(response => console.log(response))
    }

  }

  const personsToShow = filteredSearch === ''
    ? persons
    : persons.filter(person => person.name.toLowerCase().includes(filteredSearch.toLowerCase()))

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filteredSearch={filteredSearch} handleFilteredSearch={handleFilteredSearch} />
      <PersonForm
        addPerson={addPerson}
        newName={newName}
        handleNameChange={handleNameChange}
        newNumber={newNumber}
        handleNumberChange={handleNumberChange} />
      <h3>Numbers</h3>
      <Persons
        persons={personsToShow}
        deletePerson={deletePerson} />

      ...
    </div>
  )
}

export default App
