import { useState, useEffect } from 'react'
import personService from './services/persons'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import Persons from './components/Persons'
import Notification from './components/Notification'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newPerson, setNewPerson] = useState({ name: "", number: "" })
  const [filteredSearch, setFilteredSearch] = useState('')
  const [notification, setNotification] = useState(null)

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      })
  }, [])

  const handleFilteredSearch = (e) => {
    //console.log(e.target.value)
    setFilteredSearch(e.target.value)
  }

  const handleChange = (e) => {
    const { name, number } = e.target
    setNewPerson({ ...newPerson, [name]: number })
  }

  const addPerson = (e) => {
    console.log(person, persons)
    e.preventDefault()
    const person = persons.find(p => p.name === newPerson.name)
    if (!person) {
      personService
        .create(newPerson)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
          setNotification(`Added ${returnedPerson.name}`)
          setTimeout(() => {
            setNotification(null)
          }, 5000)
        })
        .catch(error => {
          console.log(error.response.data.error)
        })
    }
    else {
      console.log(person, persons)
      if (window.confirm(`${newName} is already added to phonebook, replace the old
      number with ${newNumber}?`)) {
        personService
          .update()
      }

    }
    /*  axios
       .post('http://localhost:3001/persons', personObject)
       .then(response => {
         setPersons(persons.concat(response.data))
         setNewName('')
         setNewNumber('')
       }) */
    /*  request.then(response => response.data)
     setPersons(persons.concat(personObject))
     setNewName('')
     setNewNumber('') */
  }

  const deletePerson = id => {
    let person = persons.find(p => p.id === id)
    person = person.name
    let result = window.confirm(`Delete ${person} ?`)
    if (result === true) {


      console.log(`delete id:${id}`)

      /*  const changedPerson = {...person, } */
      personService
        .remove(id)
        .then(returnedPerson => {
          setPersons(persons.filter(person => person.id !== id))
        })
        .catch(error => {
          setNotification(`Information of ${person} has already been removed from server`)
          console.log(error)
        })
      /*  .then(response => console.log(response)) */
    }

  }

  const personsToShow = filteredSearch === ''
    ? persons
    : persons.filter(person => person.name.toLowerCase().includes(filteredSearch.toLowerCase()))

  return (
    <div>
      <h2>Phonebook</h2>
      <Notification message={notification} />
      <Filter filteredSearch={filteredSearch} handleFilteredSearch={handleFilteredSearch} />
      <PersonForm
        addPerson={addPerson}
        newPerson={newPerson}
        handleChange={handleChange}
       /*  newName={newName}
        handleNameChange={handleNameChange}
        newNumber={newNumber}
        handleNumberChange={handleNumberChange} */ />
      <h3>Numbers</h3>
      <Persons
        persons={personsToShow}
        deletePerson={deletePerson} />

      ...
    </div>
  )
}

export default App
