import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filterContact, setFilterContact] = useState(false)
  const [filteredSearch, setFilteredSearch] = useState('')

  const handleFilteredSearch = (e) => {
    console.log(e.target.value)
    setFilterContact(true)
    setFilteredSearch(e.target.value)
  }

  const handleNameChange = (e) => {
    setNewName(e.target.value)

    persons.map((person) => {
      if (person.name.toLowerCase() === e.target.value.toLowerCase()) {
        //console.log(person.name, e.target.value)
        /* return */ alert(`${person.name} is already added to phonebook`)
      }
    })
    //console.log(newName)
  }
  const handleNumberChange = (e) => {
    setNewNumber(e.target.value)

    persons.map((person) => {
      if (person.number === e.target.value) {
        //console.log(person.name, e.target.value)
        /* return */ alert(`${person.number} is already added to phonebook`)
      }
    })
    //console.log(newName)
  }
  const addContact = (e) => {
    e.preventDefault()
    /*     persons.map((person) => {
          if (person.name.toLowerCase() === e.target.value.toLowerCase() || person.number === e.target.value) {

            if (person.number === e.target.value) {
              //console.log(person.name, e.target.value)
              alert(`${person.number} is already added to phonebook`)
              setNewName('')
            }
            if (person.name.toLowerCase() === e.target.value.toLowerCase()) {
              //console.log(person.name, e.target.value)
              alert(`${person.name} is already added to phonebook`)
              setNewNumber('')
            }
            return
          }
        }) */
    const contactObject = {
      name: newName,
      number: newNumber,
      id: newName,
    }
    setPersons(persons.concat(contactObject))
    setNewName('')
  }

  const contactsToShow = filterContact
    ? persons
    : persons.filter(person => person.name.toLowerCase() === filteredSearch.toLowerCase())

  return (
    <div>
      <h2>Phonebook</h2>
      <p>filter shown with <input value={filteredSearch} onChange={handleFilteredSearch} /></p>
      <form onSubmit={addContact}>
        <h2>add a new</h2>
        <div>
          name: <input value={newName} onChange={handleNameChange} />
        </div>
        <div>number: <input value={newNumber} onChange={handleNumberChange} /></div>
        {/* <div>debug: {newName}</div> */}
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map((person) => <li key={person.id}>{person.name} {person.number}</li>)}
      ...
    </div>
  )
}

export default App
